INSERT INTO tboardcomment
(BOARD_NO, COMMENT_NO, INSERT_USER_NO, INSERT_DATE, MODIFY_USER_NO, MODIFY_DATE)
VALUES (#{boardno}, #{commentno}, #{commenterno}, now(), #{commenterno}, now());
